import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios';

// https://fakestoreapi.com/products
const FetchApi = () => {
    // const [myData, setMyData] = useState([]);
    // const [showError, setShowError] = useState("");
    const [store, setStore] = useState([]);
    const [newErr, setNewErr] = useState("");

    const [text, setText] = useState("");


    // console.log(store)
    // get post put delete are methods to work with

    //axios 
    // useEffect(() => {
    //     axios.get("https://fakestoreapi.com/products")
    //         .then((resp) => setMyData(resp.data))
    //         .catch((error) => setShowError(error.message));
    // }, []);

    //using async await method here and catching error using try and catch method

    // useEffect(() => {
    //     getApiData();
    // }, [])
    // const getApiData = async () => {
    //     try {
    //         const resp = await axios.get("https://fakestoreapi.com/products");
    //         setMyData(resp.data);
    //     } catch (error) {
    //         setShowError(error.message);
    //     }
    // }
    //new
    useEffect(() => {
        newDataFromApi();
    }, []);
    const newDataFromApi = async () => {
        try {
            const resp = await axios.get("https://fakestoreapi.com/products");
            setStore(resp.data);
        }
        catch (error) {
            setNewErr(error.message);
        }
    }
    let deleteRow = (id) => {
        fetch(`https://fakestoreapi.com/products${id}`, {
            method: 'DELETE'
        }).then((result) => {
            result.json().then((resp) => {
                // console.log(resp)
            })
        })
    }
    let GetValue = () => {
        console.log(text);
        let data = { text };
        fetch("https://fakestoreapi.com/products", {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body:JSON.stringify(data)
        }).then((result)=>{
            console.log("Result >")
        })

    }
    return (
        <>
            <div className=' container '>
                <input type="text" value={text} onChange={(e) => setText(e.target.value)} max={100} />
                <button onClick={GetValue}>Click</button>
            </div>
            {store.slice(0, 10).map((elem, ind) => {
                return (
                    <div className="container">
                        <div className='row d-flex justify-content-around mx-5 py-1 bg-secondary '>
                            <div className='col-8 bg-white rounded' key={elem.id} >
                                <p width="50%"> {elem.id} . {elem.title} </p>
                            </div>
                            <button className='col-1 rounded bg-danger text-white border-0' width="50%" onClick={() => deleteRow(elem.id)}>Click</button>
                        </div>
                    </div>
                )
            })}
        </>
    )
}
export default FetchApi
